SELECT
,pt.ptdamc , u.USERNAME user_name, bm.bmmc, kehu.khmc , ck.ckmc , kly.khlymc , lly.lllymc , dlx.djlxmc ,qy.khqymc, xsmx.id mxid , xsmx.sp_id , sp.spdm , sp.spmc , sp.memo ,xsmx.jhsl,xsmx.tzsl,xsmx.cksl,xsmx.dj,xsmx.je,xsmx.zk,xsmx.xsje,dw.jldwmc FROM order_xs xs LEFT JOIN basic_ptda pt on xs.org_id = pt.id LEFT JOIN t_user u on xs.user_id = u.USER_ID LEFT JOIN system_bumeng bm on xs.bumeng_id = bm.id LEFT JOIN system_kehu kehu on xs.kehu_id = kehu.id LEFT JOIN system_cangku ck on xs.cangku_id = ck.id LEFT JOIN basic_khly kly on xs.khly_id = kly.id LEFT JOIN basic_llly lly on xs.llly_id = lly.id LEFT JOIN basic_djlx dlx on xs.djlx_id = dlx.id LEFT JOIN basic_khqy qy on xs.khqy_id = qy.id LEFT JOIN order_xsmx xsmx on xs.id = xsmx.pid LEFT JOIN shangpin sp on xsmx.sp_id = sp.id LEFT JOIN basic_jldw dw on sp.jldw_id = dw.id
and xs.deleted = #{deleted}
and xs.djbh like CONCAT('%',#{djbh},'%')
and xs.xdrq between #{startTime} and #{endTime}
and xs.sctk = #{sctk}
and xs.org_id = #{orgId}
and pt.ptdamc like CONCAT('%',#{ptdamc},'%')
and xs.sfjj = #{sfjj}
and xs.sfdz = #{sfdz}
and xs.qr = #{qr}
and xs.qrr like CONCAT('%',#{qrr},'%')
and xs.zx = #{zx}
and xs.zxr like CONCAT('%',#{zxr},'%')
and xs.zf = #{zf}
and kehu.khmc like CONCAT('%',#{khmc},'%')
and lly.lllymc like CONCAT('%',#{lllymc},'%')
and kly.khlymc like CONCAT('%',#{khlymc},'%')
and dlx.djlxmc like CONCAT('%',#{djlxmc},'%')
and qy.khqymc like CONCAT('%',#{khqymc},'%')
and u.USERNAME like CONCAT('%',#{userName},'%')
and xs.wldh like CONCAT('%',#{wldh},'%')
and xs.sh = #{sh}
and xs.shr like CONCAT('%',#{shr},'%')
and xs.zdr like CONCAT('%',#{zdr},'%')
and xs.bmmc like CONCAT('%',#{bmmc},'%')
and xs.ckmc like CONCAT('%',#{ckmc},'%')
and xs.spmc like CONCAT('%',#{spmc},'%')

SELECT

,pt.ptdamc , u.USERNAME user_name, bm.bmmc, kehu.khmc , ck.ckmc ,
kly.khlymc , lly.lllymc , dlx.djlxmc ,qy.khqymc,
xsmx.id mxid , xsmx.sp_id , sp.spdm , sp.spmc , sp.memo
,xsmx.jhsl,xsmx.tzsl,xsmx.cksl,xsmx.dj,xsmx.je,xsmx.zk,xsmx.xsje,dw.jldwmc
FROM order_xs xs
LEFT JOIN basic_ptda pt on xs.org_id = pt.id
LEFT JOIN t_user u on xs.user_id = u.USER_ID
LEFT JOIN system_bumeng bm on xs.bumeng_id = bm.id
LEFT JOIN system_kehu kehu on xs.kehu_id = kehu.id
LEFT JOIN system_cangku ck on xs.cangku_id = ck.id
LEFT JOIN basic_khly kly on xs.khly_id = kly.id
LEFT JOIN basic_llly lly on xs.llly_id = lly.id
LEFT JOIN basic_djlx dlx on xs.djlx_id = dlx.id
LEFT JOIN basic_khqy qy on xs.khqy_id = qy.id
LEFT JOIN order_xsmx xsmx on xs.id = xsmx.pid
LEFT JOIN shangpin sp on xsmx.sp_id = sp.id
LEFT JOIN basic_jldw dw on sp.jldw_id = dw.id


        xs.deleted = #{deleted}


        xs.djbh like CONCAT('%',#{djbh},'%')


        xs.xdrq between #{startTime} and #{endTime}


        xs.sctk = #{sctk}


        xs.org_id = #{orgId}


        xs.sfjj = #{sfjj}


        xs.qr = #{qr}


        xs.zx = #{zx}


        xs.zf = #{zf}


        xs.qr = #{qr}


 limit 1
